+  .left-intro img, .left-intro p {
+    max-width: 100%;
+    margin-bottom: 10px;
+  }
+
+  h3, .link {
+    font-size: 1.6rem;
+  }
+  
+  p {
+    font-size: 1.2rem;
+  }
