+  .thank-you-wrapper {
+    padding: 30px;
+    width: 100%;
+  }
+  
+  /* Title Styling */
+  .thank-you-title {
+    margin-bottom: 20px;
+    opacity: .9;
+  }
+  
+  /* Main Message Styling */
+  .thank-you-message, p {
+    font-size: 1.2rem;
+  }
+
+}

