  cursor: pointer;
  margin: 0;
  text-transform: uppercase;
}

.menu-button:hover {
  border: 1px solid #02228C;
  background-color: #02228C;
}

.vault-button {
  position: absolute;
  height: 32px;
  top: 30px;
  right: 120px; /* Positions it next to the "Home" button */
  font-family: "Inter", sans-serif;
  font-weight: 600;
  letter-spacing: .02rem;
  font-size: 11px;
  padding: 4px 20px;
  background-color: #3C0500; 
  color: #FCF6E6;
  border: 1px solid #3C0500; /* Brown border */
  border-radius: 100px;
  cursor:default;
  text-transform: uppercase;
}

.vault-button:hover {
  background-color: #3C0500; 
  color: #FCF6E6;
}

.overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  color: #511A06;
  background-color: #FCF6E6;
  z-index: 1000;
  transition: opacity 0.05s ease-in-out, visibility 0.05s ease-in-out;
  opacity: 0;
  visibility: hidden;
}

/* When overlay is active */
.overlay.open {
  opacity: 1;
  visibility: visible;
}

.overlay-content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  color: #511A06;
  top: 20px;
  left: 20px;
  text-align: left;
  gap: 30px;
}

.nav-item {
  width: 350px;
  padding: 15px 20px 20px 20px;
  border-radius: 16px;
  text-align: center;
  color: #02228C;
  cursor: pointer;
  text-decoration: none;
}

.nav-item:hover {
  background-color: #FBF2DC;
}

.nav-item-vault {
  width: 350px;
  padding: 20px;
  text-align: center;
  cursor: pointer;
}

.nav-item-vault:hover {
  background-color: #FBF2DC;
}

.nav-title, a {
  color: #02228C;
  margin-bottom: 5px;
  font-size: .7rem;
  font-weight: 600;
  position: relative;
  display: inline-flex;
  align-items: center;
  text-decoration: underline;
}

/* Style for the SVG arrows */
.nav-icon {
  width: 8px; /* Adjust size */
  height: auto;
  margin-left: 7px; /* Spacing from text */
  display: inline-block;
}


.nav-description {
  color:  #02228C;
  font-weight: 400;
  font-size: .8rem;
}

.overlay-contactitem {
  display: flex;
  flex-direction: column;
  justify-content: top;
  gap: 10px;
  padding-bottom: 30px;
}

.grid-container {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: top;
  padding: 30px;
  gap: 20px;
  text-align: left;
}

.left-intro {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  gap: 20px;
  max-width: 450px;
}

.maincontent {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  width: 90vw; /* Default width at 70% of the viewport width */
  margin: 0 auto; /* Centers the container */
  gap: 20px;
}

.main-frame {
  background-color:#F8F5EC;
  align: center;
  border-radius: 1.5rem;
  padding: 30px; /* Adds inner spacing */
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

/* Hero Section */
.hero {
  text-align: center;
  width: 100%;

}

.hero img {
  width: 100%;
  display: block;
  border-radius: 1rem;
}

/* Mission Statement */
.mission {
  display: flex;
  flex-direction: column;
  text-align: center;
  margin: 30px auto;
  max-width: 70vw;
  height: 70vh;
  justify-content: center;
  color: #02228C;
}

/* Founders Section */
.meet-founders {
  display: flex;
  flex-direction: row;
  text-align: center;
  margin: 30px auto;
  max-width: 70vw;
  height: 70vh;
  justify-content: top;
  align-items: baseline;
}

.meet-founders-content {
  display: flex;
  flex-direction: column;
  max-width: 20%;
}

.founders-headshot {
  display: flex;
  flex-direction: column;
  flex-grow: 1;
}

/* Statement Section */
.statement {
  text-align: center;
  font-size: 1.5rem;
  margin: 40px auto;
}

/* CTA */
.cta {
  text-align: center;
  padding: 20px;
}

.cta a {
  color: brown;
  font-weight: bold;
}

/* Desktop Styles (large screens) */
@media (min-width: 1131px) {
  h3, .link {
    font-size: 1.9rem;
  }

  p {
    font-size: 1rem;
  }
}

@media (min-width: 941px) and (max-width: 1130px) {
  h3, .link {
    font-size: 1.6rem;
  }
}

/* Mobile Styles (small screens) */
@media (max-width: 940px) {
  .grid-container {
    flex-direction: column;
    gap: 20px;
  }
  .left-intro {
    gap: 10px;
    padding-bottom: 20px;
    max-width: 100%;
  }

  .right-contact {
    flex-direction: column;
    justify-content: flex-start;
  }

  .left-intro img, .left-intro p {
    max-width: 100%;
    margin-bottom: 10px;
  }

  h3, .link {
    font-size: 1.6rem;
  }

  p {
    font-size: 1.2rem;
  }
}

@media (max-width: 660px) {
  h3, .link {
    font-size: 1.8rem;
  }

  #emailInput, #submitButton {
    font-size: 16px;
    width: 320px;
  }

  .thank-you-wrapper {
    padding: 30px;
    width: 100%;
  }

  /* Title Styling */
  .thank-you-title {
    margin-bottom: 20px;
    opacity: .9;
  }

  /* Main Message Styling */
  .thank-you-message, p {
    font-size: 1.2rem;
  }

}
