+                        <h4 class="nav-title">
+                            <a>The Vault <span class="nav-arrow">
+                                <img src="Lock.svg" alt="Arrow" class="nav-icon">
+                            </span></a>
+                        </h4>
+                    </div>
+                    
+                </div>   
+        </div>
+
