+        menuButtons.forEach(button => {
+            button.textContent = "Menu"; // Change text back to "Menu"
+        });
+    } else {
+        overlay.classList.add("open"); // Show overlay
+        menuButtons.forEach(button => {
+            button.textContent = "Home"; // Change text to "Home"
+        });
+    }
+}
