+            const speedThreshold = 30;
+
+            document.addEventListener('mousemove', (event) => {
+                const distance = Math.sqrt(Math.pow(event.clientX - lastX, 2) + Math.pow(event.clientY - lastY, 2));
+                lastX = event.clientX;
+                lastY = event.clientY;
+
+                if (distance > speedThreshold) {
+                    cycleLogo();
+                }
+            });
+        }
+
