+    let spinner = document.getElementById("loadingSpinner");
+    let checkmark = document.getElementById("checkmark");
+    let statusText = document.getElementById("statusText");
+    let emailContainer = document.querySelector(".email-container");
+
+    let googleScriptURL = "https://script.google.com/macros/s/AKfycbwkd41HgMaJHEcNo3e8jcB5yDj5FpBDKrqC8NUQHe-BFKJdu6iHSagNS3mdkPBxm3UE/exec";
+
+    if (emailInput.includes("@") && emailInput.includes(".")) {
+        // Store last scroll position before locking downward scroll
+        lastScrollTop = window.scrollY;
+
+        // Show loading state
+        buttonText.style.display = "none";
+        spinner.style.display = "inline-block";
+        statusText.textContent = "Saving...";
+
+        fetch(googleScriptURL, {
+            method: "POST",
+            mode: "no-cors",
+            headers: { "Content-Type": "application/x-www-form-urlencoded" },
+            body: `email=${encodeURIComponent(emailInput)}`
+        }).then(() => {
+            // Show success message
+            spinner.style.display = "none";
+            checkmark.style.display = "inline-block";
+            statusText.textContent = "Done";
+
+            setTimeout(() => {
+                emailContainer.style.opacity = "0";
+                setTimeout(() => {
+                    emailContainer.innerHTML = `
+    <div class="thank-you-wrapper">
+        <h4 class="thank-you-title">THANKS FOR REACHING OUT</h4>
+        <p class="thank-you-message">
+            We're excited to learn more about your venture and to determine how we can best partner with you on creating something 
+            <span style="font-weight: 400;">meaningful</span> and <span style="font-weight: 400;">impactful.</span>
+            <br><br>We'll be in touch shortly.
+        </p>
+    </div>`;
+
+                    emailContainer.style.opacity = "1";
+                }, 150);
+            }, 500);
+
+            document.getElementById("emailInput").value = "";
+
+            // **Lock scrolling downward but allow scrolling up**
+            window.addEventListener("scroll", function () {
+                if (window.scrollY > lastScrollTop) {
+                    window.scrollTo(0, lastScrollTop); // Prevent scrolling down
+                }
+            });
+
+        }).catch((error) => {
+            buttonText.style.display = "inline";
+            spinner.style.display = "none";
+            statusText.textContent = "";
+            alert("An error occurred. Please try again.");
+        });
+    } else {
+        alert("Please enter a valid email.");
+    }
+});
+
+    </script>    
+</body>
+</html>

